import { Collection, Db } from 'mongodb';
/**
 * MongoDB related operations.
 */
export default class MongoDb {
    /**
     * Test if a MongoDB service is running at the specified url.
     */
    static isServerAvailable(serverUrl: string): Promise<boolean>;
    static resetDatabase(serverUrl: string, databaseName: string): Promise<boolean>;
    static createCollectionIfNotExist(db: Db, collectionName: string): Promise<Collection<any>>;
}
